import { useState } from 'react';

export default function EditableTitle({initialTitle}) {
  const [title, setTitle] = useState(initialTitle)
  const [styles, setStyles] = useState({
    backgroundColor: '#EBECF0',
    borderColor: '#EBECF0',
  })

  const onBlur = (e) => {
    if (e.target.value.trim() === '') setTitle('')
    setStyles({backgroundColor: '#EBECF0', borderStyle: 'none'})
  }

  const onFocus = (e) => {
    setStyles({backgroundColor: 'white', borderColor: 'rgb(23, 43, 77)'})
  }

  return (
    <div>
    <input
      onBlur={onBlur}
      onFocus={onFocus}
      className='editable-input' 
      value={title} 
      placeholder='Title'
      onChange={e => setTitle(e.target.value)}
      type='text'
      name='title'
    > 
    </input>
    <style jsx>{`
      textarea, input { 
        outline: none; 
        padding: 2px;
      }
      input {
        border: 2px solid ${styles.borderColor};
        border-radius: 3px;
        width: 80%;
        background-color: ${styles.backgroundColor};
        font-size: 1.5rem;
      }
    `}</style>  
    </div>
  );
}
