import { useState } from 'react';
import List from '../components/List'


export default function NewList({lists, setLists}) {

   const [title, setTitle] = useState("")
   const newLists = lists.slice()

  return (
    <div>
      <form>
        <input 
          value={title} 
          placeholder='List title'
          onChange={e => setTitle(e.target.value)}
          type='text'
          name='title'
        > 
        </input>
        <button onClick={(e) => {
          e.preventDefault()
          newLists.push(<List lists={lists} setLists={setLists} key={Math.random()} title={title}/>)
          setTitle("")
          setLists(newLists)

        }}>
          Click me
        </button>
      </form>
    </div>
  );
}
