import { useState } from 'react';

export default function EditableTitle({initialTitle}) {

  const [titleElement, setTitleElement] = useState(<h2>{initialTitle}</h2>)
  const [title, setTitle] = useState(initialTitle)

  const makeEditable = () => {
    setTitleElement()
  }

  const blur = () => {

  }

  return (
    <div onClick={makeEditable} onBlur={blur}>
     <input 
      value={title} 
      placeholder='List title'
      onChange={e => setTitle(e.target.value)}
      type='text'
      name='title'
    > 
    </input>
    </div>
  );
}
