import { useState } from 'react';

export default function EditableTitle({initialTitle}) {
  const [title, setTitle] = useState(initialTitle)

  return (
    <div>
    <input
      class='editable-input' 
      value={title} 
      placeholder='Title'
      onChange={e => setTitle(e.target.value)}
      type='text'
      name='title'
    > 
    </input>
    <style jsx>{`
      textarea, input { 
        outline: none; 
      }
      input {
        border: hidden;
        width: 80%;
        background-color: yellow;
      }
    `}</style>  
    </div>
  );
}
