import { useState } from 'react';
import List from '../components/List'


export default function NewList({addList, deleteList, switchLists, addOpenMenu}) {
  const [title, setTitle] = useState('')
  const uniqueId = String(Date.now())


  return (
    <div>
      <form>
        <input 
          value={title} 
          placeholder='List title'
          onChange={e => setTitle(e.target.value)}
          type='text'
          name='title'
        > 
        </input>
        <button onClick={(e) => {

          e.preventDefault()
          addList(<List addOpenMenu={addOpenMenu} menuId={'e'.concat(uniqueId)} editorId={'e'.concat(uniqueId)} setTitle={setTitle} switchLists={switchLists} deleteList={deleteList} key={Math.random()} id={Math.random()} initialTitle={title || "Title"}/>)
          setTitle("")
          // console.log(lists.length)
          // console.log(newLists)
        }}>
          Click me
        </button>
      </form>
    </div>
  );
}
