import { useState } from 'react';


export default function Index({lists, setLists}) {

   const [title, setTitle] = useState("")
   const newLists = lists.slice()

  return (
    <div>
      <form>
        <input 
          value={title} 
          placeholder='List title'
          onChange={e => setTitle(e.target.value)}
          type='text'
          name='title'
        > 
        </input>
        <button onClick={(e) => {
          e.preventDefault()
          newLists.push( {key: Math.random(), title: title})
          setTitle("")
          setLists(newLists)
        }}>
          Click me
        </button>
      </form>
    </div>
  );
}
