import { useState } from 'react';


export default function List({title, itemKey, lists, setLists}) {

  const [items, setItems] = useState([])
  const [newItem, setNewItem] = useState('')


  const drag = (event) => {
    event.dataTransfer.setData('text', event.target.parentNode.id)
    // console.log(event.target.parentNode)
  }

  return (
    <div onDragStart={() => drag(event)} draggable='true' key={itemKey} className="list">
      <h2>{title}</h2>
      <form>
        <input 
          value={newItem} 
          placeholder='New item'
          onChange={e => setNewItem(e.target.value)}
          type='text'
          name='newItem'
        > 
        </input>
        <button onClick={(e) => {
          const newItems = items.slice()
          e.preventDefault()
          newItems.push( {key: Math.random(), content: newItem})
          setNewItem("")
          setItems(newItems)
        }}>
          Click me
        </button>
        <button onClick={(e) => {
          console.log(itemKey)
          const newLists = lists.slice().filter(list => list.key != key)
          console.log(newLists)

          setLists(newLists)
        }}>
          Delete
        </button>
        {items.map(item => <div key={item.key}>{item.content}</div>)}
      </form>
      <style jsx>{`
        .list {
          height: 100px;

          width: 100px;
          border: 1px solid red;
          display: inline-block;
          background-color: yellow;
        }
        h2 {
          font-size: 18px;
        }
      `}</style>
    </div>
  );
}
