import { useState } from 'react';


export default function Target(props) {

  const allowDrop = (event) => {
    event.preventDefault();
    console.log("allowed")
  }

  const drop = (event) => {
    event.preventDefault();
    console.log('dropped')
  }

  return (
    <div onDrop={() => drop(event)} onDragOver={() => allowDrop(event)} className="target">
      {props.children}
      <style jsx>{`
       .target {
          width: 100px;
          height: 80vh;
          border: 1px solid blue;
          background-color: red;

          z-index: -10;
       }
      `}</style>
    </div>
  );
}
