import { useState } from 'react';


export default function Target({children, targetId}) {

  const onDragOver = (event) => {
    event.preventDefault();
    // const data = event.dataTransfer.getData('text')
    // console.log(data)
  }

  const onDrop = (event) => {
    event.preventDefault();

    const targetNode = event.target
    const sourceNode = document.getElementById(event.dataTransfer.getData('text'))
    console.log(targetNode)
    const sourceData = sourceNode.firstChild
    const targetData = event.target.firstChild
    sourceNode.append(targetData)
    targetNode.append(sourceData)

  }

  return (
    <div id={targetId} onDrop={() => onDrop(event)} onDragOver={() => onDragOver(event)} className="target">
      {children}
      <style jsx>{`
       .target {
          width: 100px;
          height: 80vh;
          border: 1px solid blue;
          background-color: red;
       }
      `}</style>
    </div>
  );
}
