import { useState, useEffect } from 'react'
import EditableTitle from './EditableTitle'



export default class List extends React.Component({ initialTitle, id, deleteList, switchLists }) {

  constructor(props){
    super(props)
    this.state = {
      editorId: 'x'.concat(String(Date.now())),
      content: null,
      editor: null,
    }
  }

  componentDidMount(){
    const  quill = new Quill(`#${editorId}`, {
      theme: 'snow'
    })

    quill.on('text-change', () => {
      this.setState({ contents: quill.getContents()})
      console.log(this.state.content)
    });
  }



  drag = (event) => {
    event.dataTransfer.setData('text', event.target.id)
  }

  onDragOver = (event) => {
    event.preventDefault();
  }

  onDrop = (event) => {
    event.preventDefault();
    const sourceNodeId = event.dataTransfer.getData('text')
    const targetNodeId = event.target.closest('.list').id
    switchLists(sourceNodeId, targetNodeId)
  }
  render(){
    return (
      <div onDrop={() => onDrop(event)} onDragOver={() => onDragOver(event)} onDragStart={() => drag(event)} draggable='true' id={id} className="list">
        <EditableTitle initialTitle={initialTitle}/>
        <div>
          <button onClick={(e) => {
            e.preventDefault()
            deleteList(id)
          }}>
            Delete
          </button>
          <div id={editorId}>
            <p>Write notes here....</p>
            <p><br/></p>
          </div>
        </div>
        <style jsx>{`

          .list {
            vertical-align: top;
            width: 250px;
            border: 1px solid red;
            display: inline-block;
            background-color: yellow;
            flex-shrink: 0;
          }
          h2 {
            font-size: 18px;
          }
          input {
            width: 80%;
          }
        `}</style>
      </div>
    );
  }

}
