import { useState } from 'react';
import List from '../components/List'


export default function NewList({addList}) {
  // console.log(lists)
   const [title, setTitle] = useState("")
   const newLists = lists.slice()

  return (
    <div>
      <form>
        <input 
          value={title} 
          placeholder='List title'
          onChange={e => setTitle(e.target.value)}
          type='text'
          name='title'
        > 
        </input>
        <button onClick={(e) => {

          e.preventDefault()
          addList(<List lists={lists} setLists={setLists} itemKey={Math.random()} title={title}/>)
          setTitle("")
          // console.log(lists.length)
          // console.log(newLists)
        }}>
          Click me
        </button>
      </form>
    </div>
  );
}
