import { useState, useEffect } from 'react'
import EditableTitle from './EditableTitle'



export default function List({ initialTitle, id, deleteList, switchLists }) {

  const editorId = 'x'.concat(String(Date.now()))
  const [content, setContent] = useState({})
  const [editor, setEditor] = useState(false)

  useEffect(() => {
    if (editor === false) {
      const  quill = new Quill(`#${editorId}`, {
        theme: 'snow'
      })
      setEditor(editor)
    }

    // quill.on('text-change', () => {
    //   setContent(quill.getContents())
    //   console.log(content)
    // });
  }, [content, editor]);



  const drag = (event) => {
    event.dataTransfer.setData('text', event.target.id)
  }

  const onDragOver = (event) => {
    event.preventDefault();
  }

  const onDrop = (event) => {
    event.preventDefault();
    const sourceNodeId = event.dataTransfer.getData('text')
    const targetNodeId = event.target.closest('.list').id
    switchLists(sourceNodeId, targetNodeId)
  }

  return (
    <div onDrop={() => onDrop(event)} onDragOver={() => onDragOver(event)} onDragStart={() => drag(event)} draggable='true' id={id} className="list">
      <EditableTitle initialTitle={initialTitle}/>
      <div>
        <button onClick={(e) => {
          e.preventDefault()
          deleteList(id)
        }}>
          Delete
        </button>
        <div id={editorId}>
          <p>Write notes here....</p>
          <p><br/></p>
        </div>
      </div>
      <style jsx>{`

        .list {
          vertical-align: top;
          width: 250px;
          border: 1px solid red;
          display: inline-block;
          background-color: yellow;
          flex-shrink: 0;
        }
        h2 {
          font-size: 18px;
        }
        input {
          width: 80%;
        }
      `}</style>
    </div>
  );
}
