import { useState } from 'react';

export default function EditableTitle({initialTitle}) {
  const [title, setTitle] = useState(initialTitle)

  return (
     <input
      class='editable-input' 
      value={title} 
      placeholder='Title'
      onChange={e => setTitle(e.target.value)}
      type='text'
      name='title'
    > 
    <style jsx>{`
      .editable-input {
        border: none;
      }
    `}</style>
    </input>
  );
}
