import { useState } from 'react';


export default function List({ initialTitle, id, deleteList, switchLists }) {

  const [items, setItems] = useState([])
  const [newItem, setNewItem] = useState('')
  const [title, setTitle] = useState(initialTitle)
  const [titleElement, setTitleElement] = useState(<h2>{title}</h2>)

  const drag = (event) => {
    event.dataTransfer.setData('text', event.target.id)
  }

  const onDragOver = (event) => {
    event.preventDefault();
  }

  const onDrop = (event) => {
    event.preventDefault();
    const sourceNodeId = event.dataTransfer.getData('text')
    const targetNodeId = event.target.closest('.list').id
    switchLists(sourceNodeId, targetNodeId)
  }

  const makeTitleEditable = () => {
    setTitleElement(<input 
      value={title} 
      placeholder='Title'
      onChange={e => setTitle(e.target.value)}
      type='text'
      name='title'
    > 
    </input>)
  }

  return (
    <div onDrop={() => onDrop(event)} onDragOver={() => onDragOver(event)} onDragStart={() => drag(event)} draggable='true' id={id} className="list">
    <EditableTitle />
      <form>
        <input 
          value={newItem} 
          placeholder='New item'
          onChange={e => setNewItem(e.target.value)}
          type='text'
          name='newItem'
        > 
        </input>
        <button onClick={(e) => {
          const newItems = items.slice()
          e.preventDefault()
          newItems.push( {key: Math.random(), content: newItem})
          setNewItem("")
          setItems(newItems)
        }}>
          Click me
        </button>
        <button onClick={(e) => {
          e.preventDefault()
          deleteList(id)
        }}>
          Delete
        </button>
        {items.map(item => <div key={item.key}>{item.content}</div>)}
      </form>
      <style jsx>{`
        .list {
          height: 100px;
          width: 100px;
          border: 1px solid red;
          display: inline-block;
          background-color: yellow;
        }
        h2 {
          font-size: 18px;
        }
      `}</style>
    </div>
  );
}
