import NewList from '../components/NewList'
import List from '../components/List'
import Target from '../components/Target'
import { useState } from 'react'

export default class Index extends React.Component {
  constructor(props){
    super(props)

    this.state = {
      lists: []
    }

  }

  addList = (list) => {
    let newLists = this.state.lists.slice()
    newLists.push(list)
    this.setState({lists: newLists})
  }

  deleteList = (listId) => {
    let newLists = this.state.lists.slice()
    newLists = newLists.filter(list => list.props.itemKey !== listId)
    this.setState({lists: newLists})
  }

  switchLists = (sourceId, targetId) => {
    let newLists = this.state.lists.slice()
    let sourceElement
    let targetElement
    newLists.forEach(list => {
      if (String(list.props.id) === String(sourceId)){
        sourceElement = list
      }
      if (String(list.props.id) === String(targetId)){
        targetElement = list
      }
    })


    newLists = newLists.map(list => {
      // console.log(list)
      // console.log(String(sourceId))
      // console.log(String(targetId))
      if (String(list.props.id) === String(sourceId)){
        return targetElement
      }
      if (String(list.props.id) === String(targetId)){
        return sourceElement
      }
      return list
    })
    // console.log(newLists)
    this.setState({lists: newLists})
  }

  render(){
    return (
      <div>
        <p>Hello Next.js</p>
        <NewList switchLists={this.switchLists} deleteList={this.deleteList} addList={this.addList}/>
        <div className='lists-container'>
          {this.state.lists}
        </div>

        <style jsx>{`

          .dropspace {
            border: 1px solid blue;
            display: inline-block;
            height: 100px;
            width: 100px;
          }
          .item {
            border: 1px solid red;
            display: inline-block;
          }
          .targets-container {
            position: absolute;
            display: flex;
          }
          .lists-container {
            display: flex;
          }
        `}</style>
      </div>
    )
  }
}
