import { useState } from 'react';

export default function EditableTitle({initialTitle}) {
  const [title, setTitle] = useState(initialTitle)
  const [backgroundColor, setBackroundColor] = useState("yellow")

  const onBlur = (e) => {
    if (e.target.value.trim() === '') setTitle('')
  }

  return (
    <div>
    <input
      onBlur={onBlur}
      className='editable-input' 
      value={title} 
      placeholder='Title'
      onChange={e => setTitle(e.target.value)}
      type='text'
      name='title'
    > 
    </input>
    <style jsx>{`
      textarea, input { 
        outline: none; 
      }
      input {
        border: hidden;
        width: 80%;
        background-color: yellow;
        font-size: 1.5rem;
      }
    `}</style>  
    </div>
  );
}
