import { useState } from 'react';


export default function Target({title, key}) {

   const [items, setItems] = useState([])
   const [newItem, setNewItem] = useState('')

  return (
    <div key={key} className="list">
      <h2>{title}</h2>
      <form>
        <input 
          value={newItem} 
          placeholder='New item'
          onChange={e => setNewItem(e.target.value)}
          type='text'
          name='newItem'
        > 
        </input>
        <button onClick={(e) => {
          const newItems = items.slice()
          e.preventDefault()
          newItems.push( {key: Math.random(), content: newItem})
          setNewItem("")
          setItems(newItems)
        }}>
          Click me
        </button>
        {items.map(item => <div key={item.key}>{item.content}</div>)}
      </form>
      <style jsx>{`
        .list {
          height: 100px;
          width: 100px;
          border: 1px solid red;
          display: inline-block;
        }
        h2 {
          font-size: 18px;
        }
      `}</style>
    </div>
  );
}
