import { useState } from 'react';


export default function List({title, key}) {

   const [items, setItems] = useState([])
   const [newItem, setNewItem] = useState('')
   const [order, setOrder] = useState()

   const drag = (event) => {


    event
      .target
      .style
      .backgroundColor = 'yellow'
   }

  return (
    <div onDragStart={() => drag(event)} draggable='true' key={key} className="list">
      <h2>{title}</h2>
      <form>
        <input 
          value={newItem} 
          placeholder='New item'
          onChange={e => setNewItem(e.target.value)}
          type='text'
          name='newItem'
        > 
        </input>
        <button onClick={(e) => {
          const newItems = items.slice()
          e.preventDefault()
          newItems.push( {key: Math.random(), content: newItem})
          setNewItem("")
          setItems(newItems)
        }}>
          Click me
        </button>
        {items.map(item => <div key={item.key}>{item.content}</div>)}
      </form>
      <style jsx>{`
        .list {
          height: 100px;
          order: ${order};
          width: 100px;
          border: 1px solid red;
          display: inline-block;
          background-color: blue;
        }
        h2 {
          font-size: 18px;
        }
      `}</style>
    </div>
  );
}
