import { useState, useEffect } from 'react'
import EditableTitle from './EditableTitle'



export default function List({ initialTitle, id, deleteList, switchLists }) {

  const [items, setItems] = useState([])
  const [newItem, setNewItem] = useState('')

  const editorId = String(Math.random())

  useEffect(() => {
    const  quill = new Quill('#editor', {
      modules: {
        toolbar: '#toolbar'
      },
      theme: 'snow'
    });
  });

  const drag = (event) => {
    event.dataTransfer.setData('text', event.target.id)
  }

  const onDragOver = (event) => {
    event.preventDefault();
  }

  const onDrop = (event) => {
    event.preventDefault();
    const sourceNodeId = event.dataTransfer.getData('text')
    const targetNodeId = event.target.closest('.list').id
    switchLists(sourceNodeId, targetNodeId)
  }

  return (
    <div onDrop={() => onDrop(event)} onDragOver={() => onDragOver(event)} onDragStart={() => drag(event)} draggable='true' id={id} className="list">
    <EditableTitle initialTitle={initialTitle}/>
      <div>
        <input 
          value={newItem} 
          placeholder='New item'
          onChange={e => setNewItem(e.target.value)}
          type='text'
          name='newItem'
        > 
        </input>
        <button onClick={(e) => {
          const newItems = items.slice()
          e.preventDefault()
          newItems.push( {key: Math.random(), content: newItem})
          setNewItem("")
          setItems(newItems)
        }}>
          Add item
        </button>
        <button onClick={(e) => {
          e.preventDefault()
          deleteList(id)
        }}>
          Delete
        </button>
        {items.map(item => <div key={item.key}>{item.content}</div>)}
        <div id={editorId}></div>
      </div>
      <style jsx>{`
        .list {
          vertical-align: top;
          width: 250px;
          border: 1px solid red;
          display: inline-block;
          background-color: yellow;
        }
        h2 {
          font-size: 18px;
        }
        input {
          width: 80%;
        }
      `}</style>
    </div>
  );
}
