import { useState } from 'react';

export default function EditableTitle({initialTitle}) {
  const [title, setTitle] = useState(initialTitle)
  const [backgroundColor, setBackroundColor] = useState('#EBECF0')

  const onBlur = (e) => {
    if (e.target.value.trim() === '') setTitle('')
    setBackroundColor('#EBECF0')
  }

  const onFocus = (e) => {
    setBackroundColor('white')
  }

  return (
    <div>
    <input
      onBlur={onBlur}
      onFocus={onFocus}
      className='editable-input' 
      value={title} 
      placeholder='Title'
      onChange={e => setTitle(e.target.value)}
      type='text'
      name='title'
    > 
    </input>
    <style jsx>{`
      textarea, input { 
        outline: none; 
      }
      input {
        border: none;
        border-radius: 3px;
        width: 80%;
        background-color: ${backgroundColor};
        font-size: 1.5rem;
      }
    `}</style>  
    </div>
  );
}
