import { useState } from 'react';
import List from '../components/List'


export default function NewList({addList, deleteList, switchLists, setOpenMenu, openMenu}) {
  const [title, setTitle] = useState('')

  return (
    <div>
      <form>
        <input 
          value={title} 
          placeholder='List title'
          onChange={e => setTitle(e.target.value)}
          type='text'
          name='title'
        > 
        </input>
        <button onClick={(e) => {
          e.preventDefault()
          addList(<List 
            openMenu={openMenu} 
            setOpenMenu={setOpenMenu} 
            setTitle={setTitle} 
            switchLists={switchLists} 
            deleteList={deleteList} 
            key={Math.random()} 
            id={Math.random()} 
            initialTitle={title || "Title"}/>)
          setTitle("")
        }}>
          Click me
        </button>
      </form>
    </div>
  );
}
